 = 'rotate(' + steps[now++] + 'deg)';        if (now < steps.length) {			running = true;            requestAnimFrame(step)        } else {            running = false;            setTimeout(function() {                if (prize != null) {                    $("#sncode").text(sncode);                    var type = "";                    if (prize == 1) {                        type = "一等奖"                    } else if (prize == 2) {                        type = "二等奖"                    } else if (prize == 3) {                        type = "三等奖"                    }					else if (prize == 4) {                        type = "四等奖"                    }					else if (prize == 5) {                        type = "五等奖"                    }					else if (prize == 6) {                        type = "六等奖"                    }                    $("#prizetype").text(type);                    $("#result").slideToggle(500);                    $("#outercont").slideUp(500)                } else {                    alert("亲，继续努力哦！")                }            },            200)        }    }    function start(deg) {        deg = deg || lostDeg[parseInt(lostDeg.length * Math.random())];        running = true;        clearInterval(timer);        totalDeg = 360 * 5 + deg;        steps = [];        now = 0;        countSteps();        requestAnimFrame(step)    }    window.start = start;    outter = document.getElementById('outer');    inner = document.getElementById('inner');    i = 10;    $("#inner").click(function() {           if (running) return;        if (count >= 5) {            alert("您已经抽了 5 次奖,不能再抽了,下次再来吧!");            return        }        if (prize != null) {            alert("亲，你不能再参加本次活动了喔！下次再来吧~");            return        }        $.ajax({            url: "index.php",            dataType: "json",            data: {                token: "o7MB9juTktqvPAPv0cY4x21n8-qc",                ac: "activityuser",                tid: "565",                t: Math.random()            },            beforeSend: function() {                running = true;                timer = setInterval(function() {                    i += 5;                    outter.style.webkitTransform = 'rotate(' + i + 'deg)';                    outter.style.MozTransform = 'rotate(' + i + 'deg)'                },                1)            },            success: function(data) {                if (data.error == "invalid") {                    alert("亲，继续努力哦！ 您已经抽了 5 次奖,不能再抽了,下次再来吧!");                    count = 5;                    clearInterval(timer);                    return                }                if (data.error == "getsn") {                    $("#tel").val(data.tel);					if(data.state==2){					$("#closedj").css("display","none");					}					$("#red").text(data.msg);                    alert(data.msg + data.sn);                    count = 5;                    clearInterval(timer);                    prize = data.prizetype;                    sncode = data.sn;                    start(prizeDeg[data.prizetype - 1]);                    return                }                if (data.success) {                    prize = data.prizetype;                    sncode = data.sn;                    start(prizeDeg[data.prizetype - 1])                } else {                    prize = null;                    start()                }                running = false;                count++            },            error: function() {                prize = null;                start();                running = false;                count++            },            timeout: 1000        })    })});$("#save-btn").bind("click",function() {    var btn = $(this);    var tel = $("#tel").val();    if (tel == '') {        alert("请输入手机号");        return    }       var submitData = {        tid: 565,        code: $("#sncode").text(),        tel: tel,        action: "setTel"    };    $.post('index.php?ac=activityuser', submitData,    function(data) {        if (data.success == true) {            alert(data.msg);            return        } else {}    },    "json")});$("#save-btnn").bind("click",function() {    var submitData = {        tid: 565,        code: $("#sncode").text(),parssword:$("#parssword").val(),        action: "setTel"    };    $.post('index.php?ac=activityuser', submitData,    function(data) {        if (data.success == true) {            alert(data.msg);if (data.changed == true) {window.location.href=location.href;}              return        } else {}    },    "json")});</script></body></html>